<script>
export default {
  name: "mobileNav",
  props: {
    curChoice: {
      default: 0,
      type: Number
    }
  },
  data() {
    return {
      cur_choice: this.curChoice
    }
  },
  methods: {
    goToHome() {
      if (this.cur_choice !== 0) {
        this.$router.push({
            path: '/home'
          }
        );
      }
    },
    goToAppliance() {
      if (this.cur_choice !== 1) {
        this.$router.push({
          path: '/appliance'
        });
      }
    },
    goToSetting() {
      if (this.cur_choice !== 2) {
        this.$router.push({
          path: '/setting'
        });
      }
    }
  },
}
</script>

<style scoped>

#mobileNavContainer {
  position: fixed;
  height: 65px;
  width: 100%;
  bottom: 0;
  background-color: rgba(75, 75, 75, 0.3);
  backdrop-filter: blur(15px);
}

.navChoice {
  position: absolute;
  top: 8px;
  width: 50px;
  height: 45px;
  border-radius: 8px;
  transform: translateX(-50%);
}

#home {
  left: 20%;
}

#appliance {
  left: 50%;
}

#setting {
  left: 80%;
}

#homeIcon, #applianceIcon, #settingIcon {
  display: block;
  margin: 2px auto;
  width: 26px;
}

.subtitle {
  font-size: 10px;
  text-align: center;
  color: white;
}

.currentChoice {
  background-color: rgba(255, 255, 255, 0.25);
}

</style>