<script>
export default {
  name: "navChoice",
  props: {
    navChoiceName: {
      type: String,
      required: true
    },
    navChoiceIconFileName: {
      required: true
    },
    navChoiceIconSize: {
      default: '22px'
    },
    navChoiceStatus: {
      type: Boolean,
      default: false
    },
    navChoiceLink: {
      default: '/'
    },
    roomId: {
      default: 0
    }
  },
  methods: {
    goToRoom(e) {
      this.$emit('changeRoom', parseInt(this.roomId) + 8);
      this.$router.push({
        path: '/room',
        query: {
          id: this.roomId
        }
      });
    }
  }
}
</script>

